mapper.select:
SELECT g.guahaoid AS id, g.jiuzhenxinxiid, g.userid, g.`name` AS xingming, '' AS bingzhong, g.paibanid, date_format(g.chuangjianshijian,'%Y-%m-%d')AS chuangjianshijian, g.shenhejieguo, g.shenheyijian, g.shenheshijian, g.zhuangtai, 'guahao' AS type, p.`name` AS zhuanjianame, pj.shenfenzhenghaoma, pj.xingbie, pj.age FROM guahao g LEFT JOIN paibanguanli p ON p.paibanid = g.paibanid LEFT JOIN patient_jiuzhenxinxi pj on pj.jiuzhenxinxiid = g.jiuzhenxinxiid WHERE g.userid = #{userid}
UNION
SELECT z.zhuyuanid AS id, z.jiuzhenxinxiid, z.userid, z.xingming, db.text AS bingzhong, '' AS paibanid, date_format(z.chuangjianshijian,'%Y-%m-%d')AS chuangjianshijian, z.shenhejieguo, z.shenheyijian, z.shenheshijian, z.zhuangtai, 'zhuyuan' AS type, '住院' AS zhuanjianame, pj.shenfenzhenghaoma, pj.xingbie, pj.age FROM zhuyuan z LEFT JOIN dict_bingzhong db on db.id = z.bingzhong LEFT JOIN patient_jiuzhenxinxi pj on pj.jiuzhenxinxiid = z.jiuzhenxinxiid WHERE z.userid = #{userid}
ORDER BY chuangjianshijian DESC

SELECT g.guahaoid, g.jiuzhenxinxiid, g.userid, g.paibanid, g.`name` AS jiuzhenxingming, date_format(g.chuangjianshijian,'%Y-%m-%d') as chuangjianshijian, g.zhuangtai, g.shenhejieguo, g.shenheshijian, g.shenheyijian, pg.expertid, pg.`name` AS zhuanjiaxingming, pg.guahaoleibie, date_format(pg.paibanriqi,'%Y-%m-%d') as paibanriqi, pg.shangxiawu, pg.xianhaoshu, pg.shengyuhaoshu, pg.jiage, pg.zhuangtai, pj.shenfenzhenghaoma, pj.xingbie, pj.zhuyaozhengzhuang, pj.shoujihao, pj.age, pj.lianxidizhi, pj.zhuangtai, bz.text as bingzhongtext, pj.nianling, nld.text as age_text FROM guahao AS g INNER JOIN paibanguanli AS pg ON g.paibanid = pg.paibanid INNER JOIN patient_jiuzhenxinxi AS pj ON g.jiuzhenxinxiid = pj.jiuzhenxinxiid left join dict_nianlingduan as nld on nld.id = pj.age left join dict_bingzhong as bz on bz.id=pj.bingzhong WHERE g.`shenhejieguo` = #{shenhejieguo} and pj.`name` LIKE CONCAT('%',#{xingming},'%')
AND pj.shoujihao LIKE CONCAT('%',#{shoujihao},'%')
AND pj.bingzhong = #{bingzhong}
AND pg.expertid = #{expertid}
AND date_format(pg.paibanriqi,'%Y-%m-%d') = #{yuyueriqi}
ORDER BY pg.paibanriqi asc
LIMIT #{startindex},#{pagesize}

SELECT count(*) FROM guahao AS g INNER JOIN paibanguanli AS pg ON g.paibanid = pg.paibanid INNER JOIN patient_jiuzhenxinxi AS pj ON g.jiuzhenxinxiid = pj.jiuzhenxinxiid WHERE g.`shenhejieguo` = #{shenhejieguo} and pj.`name` LIKE CONCAT('%',#{xingming},'%')
AND pj.shoujihao LIKE CONCAT('%',#{shoujihao},'%')
AND pj.bingzhong = #{bingzhong}
AND pg.expertid = #{expertid}
AND date_format(pg.paibanriqi,'%Y-%m-%d') = #{yuyueriqi}
ORDER BY g.chuangjianshijian DESC
