mapper.select:
SELECT zy.zhuyuanid, zy.jiuzhenxinxiid, zy.userid, zy.xingming, zy.bingzhong, zy.chuangjianshijian, zy.zhuangtai, zy.shenhejieguo, zy.shenheshijian, zy.shenheyijian, bz.id, bz.text, pj.shenfenzhenghaoma, pj.xingbie, pj.zhuyaozhengzhuang, pj.shoujihao, pj.age, pj.lianxidizhi, pj.zhuangtai, pj.chuangjianshijian FROM zhuyuan AS zy INNER JOIN dict_bingzhong AS bz ON zy.bingzhong = bz.id INNER JOIN patient_jiuzhenxinxi AS pj ON zy.jiuzhenxinxiid = pj.jiuzhenxinxiid WHERE zy.`shenhejieguo` = #{shenhejieguo} and pj.`name` LIKE CONCAT('%',#{xingming},'%')
AND pj.shoujihao LIKE CONCAT('%',#{shoujihao},'%')
AND zy.bingzhong = #{bingzhong}
AND date_format(zy.chuangjianshijian,'%Y-%m-%d') = #{yuyueriqi}
ORDER BY zy.chuangjianshijian DESC LIMIT #{startindex},#{pagesize}

SELECT count(*) FROM zhuyuan AS zy INNER JOIN dict_bingzhong AS bz ON zy.bingzhong = bz.id INNER JOIN patient_jiuzhenxinxi AS pj ON zy.jiuzhenxinxiid = pj.jiuzhenxinxiid WHERE zy.`shenhejieguo` = #{shenhejieguo} and pj.`name` LIKE CONCAT('%',#{xingming},'%')
AND pj.shoujihao LIKE CONCAT('%',#{shoujihao},'%')
AND zy.bingzhong = #{bingzhong}
AND date_format(zy.chuangjianshijian,'%Y-%m-%d') = #{yuyueriqi}
ORDER BY zy.chuangjianshijian DESC
